/**
 * Crux user guide controller
 *
 * If you don't need the user guide, you can delete this file
 * and the associated views in ./views/guide
 */

module.exports = {
	
	_notFound: function(handle) {
		var data = {
			pageView: 'guide/' + (handle.uriSegment(2) || 'index')
		};
		handle.renderView('guide/template', data, true, function() {
			handle.send();
		});
	}
	
};

/* End of file guide.js */
