/**
 * Library manager
 */

module.exports = (function() {
	var libs = { };

	libs.require = function(lib) {
		if (! libs.hasOwnProperty(lib)) {
			$.log.verbose.message('Loading library "' + lib + '"');
			libs[lib] = require($.LIB_PATH + lib + '/' + lib);
		}
		return libs[lib];
	};
	
	return libs;
}());

