/**
 * Model manager
 */

module.exports = (function() {
	var models = { };

	models.require = function(model) {
		if (! models.hasOwnProperty(model)) {
			$.log.verbose.message('Loading model "' + model + '"');
			models[model] = require($.MODEL_PATH + model);
		}
		return models[model];
	};
	
	return models;
}());

