/**
 * JavaScript for the user guide pages
 */

(function(win, doc) {
	if ('onhashchange' in win) {
	
		var sections = doc.getElementsByClassName('section');
	
		win.onload = showSection;
		win.onhashchange = showSection;
	
		function showSection() {
			var id = location.hash.slice(1) || 'index';
			if (id === 'all') {
				setAllSections('shown');
			} else {
				var elem = doc.getElementById(id);
				if (elem) {
					setAllSections('hidden');
					elem.className = 'section shown';
				}
			}
			scrollToTop();
		}
	
		function setAllSections(cn) {
			for (var i = 0, c = sections.length; i < c; i++) {
				sections[i].className = 'section ' + cn;
			}
		}
	
		var html, body;
		function scrollToTop() {
			html = html || doc.documentElement || doc.getElementsByTagName('html')[0];
			body = body || doc.body || doc.getElementsByTagName('body')[0];
			html.scrollTop = body.scrollTop = 0;
		}
	
	}
}(window, document));

/* End of file guide.js */
